def find_consecutive(lst):
    ans = []
    count = 1

    for i in range(1, len(lst)):
        if lst[i] == lst[i-1]:
            count += 1
        else:
            count = 1

        if count == 3:
            ans.append(lst[i])

    return ans

n = list(map(int,input().split(",")))
result = find_consecutive(n)
print(result)
